sourcesContent:
.appBar {
  position: fixed !important;
  top: 0px !important;
}

.logo {
  width: 48px;
  height: 48px;
  background: url(./logo.png) no-repeat center center;
  background-size: cover;
}


/** WEBPACK FOOTER **
 ** webpack:///src/views/layout/Header.scss
 **/

.footer {
  position: fixed !important;
  bottom: 0 !important;
  background-color: #f0f0f0 !important;
  border-top: 1px solid #bbbbbb !important;
  svg {
    margin: auto;
  }
}


/** WEBPACK FOOTER **
 ** webpack:///src/views/layout/Footer.scss
 **/

@media (max-width: 736px) { /* disable text selection on mobile device: iPhone 6+ portrait */
  * {
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
  }
}

body {
  font-family: 'Roboto', sans-serif;
  font-size: 13px;
  line-height: 20px;
  margin: 0px;
}

.content {
  padding: 70px 0 80px 0;
  overflow-x: hidden;
}




/** WEBPACK FOOTER **
 ** webpack:///src/views/layout/Layout.scss
 **/

.heading {
  text-align: center;
  margin: 20px;
  padding: 19px 0px 0px;
  font-weight: 300;
  font-size: 20px;
  line-height: 28px;
  letter-spacing: 0px;
  color: rgba(0, 0, 0, 0.870588);
}

.grid {
  max-width: 600px;
  margin: 0px auto;
  &:after {
    content: ' ';
    visibility: hidden;
    display: block;
    height: 0;
    clear: both;
  }
  @media (max-width: 500px) {
    max-width: 300px;
  }
}

.gridContent {
  padding: 10px;
}

.gridTitle {
  font-size: 20px;
  padding: 0px;
  margin: 0px;
  letter-spacing: 0px;
  font-weight: 500;
  color: rgba(0, 0, 0, 0.870588);
  background-color: rgb(238, 238, 238);
  text-align: center;
  line-height: 64px;
}

.gridRequirements {
  margin-bottom: 30px;
  h4 {
    margin-bottom: 0;
  }
}

.deployment {
  width: 260px;
  height: 300px;
  float: left;
  margin: 20px;
  background-color: #f6f6f6 !important;
}

.deploymentButton {
  margin-right: 20px;
}

.localTab {
  height: 200px;
  h3 {
    display: inline-table;
    width: 100%;
  }
}

.localResult {
  height: 150px;
}

.code {
  background-color: black;
  color: #c3c1bc;
  height: 100%;
  > div {
    padding: 20px;
  }
}

.stepContent {
  min-height: 200px;
}

.stepActions {
  margin-top: 10px;
  button {
    margin-right: 10px !important;
  }
}

.twoColumn {
  width: 50%;
  float: left;
  display: inline-block;
  @media (max-width: 500px) {
    width: 100%;
  }
}

.checkboxInProgress > div > div > div > div {
  top: -13px;
  left: -13px;
}


/** WEBPACK FOOTER **
 ** webpack:///src/views/starter/Starter.scss
 **/

.sectionTitle {
  color: lightgray;
  margin: 10px;
  padding: 10px;
}

.darkBackgroud {
  background-color: lightgray;
  .sectionTitle {
    color: white;
  }
}

.realtime-line {
  :global .axis {
    stroke-width: 1;

    :global .tick {
      line {
        stroke: black;
        stroke-width: 0.2;
      }
      text {
        fill: black;
        font-size: 0.7em;
      }
    }
    :global .domain {
      fill: none;
      stroke: black;
      stroke-width: 0.2;
    }
  }

  :global .group {
    fill: none;
    stroke: black;
    stroke-width: 1.5;
  }

  :global .dial {
    :global .needle path {
      fill: #b21f24;
    }

    :global circle.label {
      fill: white;
    }

    :global line.label {
      stroke: white;
      stroke-width: 1px;
    }

    :global text.label {
      font-family: Arial;
      font-size: 12px;
      fill: gray;
    }

    :global text.speed {
      fill: white;
      font-size: 24px;
    }
  }

}

.eventsOverTimeLegend span {
  display: block;
  float: right;
  width: 50px;
  height: 20px;
  font-size: 10px;
  text-align: center;
  margin: 5px;
}

.usersOverTime {
  :global .custom_padding {
    padding-left: 5px !important;
    padding-right: 5px !important;

  }

  :global .card {
    height: 125px;
    border-radius: 2px;
    font-weight: 300;
  }

  :global .bg {
    margin-bottom: 10px;
    background-color: #ffffff;
    border-radius: 4px;
    height: 350px;
  }

  :global .shadow {
    -webkit-filter: drop-shadow(0px 2px 2px rgba(0, 0, 0, .3));
    filter: drop-shadow(0px 2px 2px rgba(0, 0, 0, .3));
  }

  :global .hr-custom {
    border: 0;
    height: 1px;
    background-color: rgba(100, 100, 100, 0.2);
    margin-bottom: 10px !important;
    -background-image: linear-gradient(to right, rgba(0, 0, 0, 0), rgba(100, 100, 100, 0.5), rgba(0, 0, 0, 0));
  }

  :global .panel-header {
    height: 50px;
    background-color: #f4f4f4;
    border-radius: 2px 2px 0 0;
    border-bottom: 1px solid #dbdbdb;
    padding: 10px 20px 10px 20px;
    margin-bottom: 20px;
  }

  :global .range-custom {
    padding-bottom: 10px;
    padding-left: 5px;
  }

  :global .range-custom-child {
    line-height: 30px;
  }

  :global .range-span {
    color: #7f7f85;
    padding-right: 10px;
  }

  :global .padding-left-5 {
    padding-left: 5px;
  }

  :global .card_header {
    padding: 10px 10px 5px 10px;
    color: #ffffff;
  }

  :global .card_body {
    text-align: center;
    color: #ffffff;
    font-size: 3em;
    font-weight: 400;
  }

  :global .panel-title {
    color: #6e6e74;
    font-size: 1.5em;
    font-weight: 300;
  }

  :global .line-height-30 {
    line-height: 30px;
  }

  :global .margin-below-20 {
    margin-bottom: 20px;
  }

  :global .line {
    fill: none;
    stroke: #53c79f;
    stroke-width: 3px;

  }

  :global .axis {
    fill: #a3a3a7;
  }

  :global .axis path,
  :global .axis line {
    fill: none;
    stroke: #ececed;
    shape-rendering: crispEdges;

  }

  :global .axis text {
    font-size: 12px;
  }

  :global .y-grid .tick {
    stroke: #ececed;
    opacity: 1;
  }

  :global .y-grid path,
  :global .y-grid line {
    fill: none;
    stroke: #ececed;
    shape-rendering: crispEdges;
    opacity: 1;

  }

  :global .dot {
    fill: #FFFFFF;
    stroke: #53c79f;
    stroke-width: 2px;

  }

  :global .area {
    opacity: .9;
    stroke: #fff;
    stroke-width: 2px;
  }

  :global .tooltip-bg {
    fill: #000;
    opacity: 0.7;
  }

  :global .tooltip-text1 {
    font-size: 15px;
    fill: #ffffff;
  }

  :global .tooltip-text2 {
    font-size: 20px;
    fill: #a9f3ff;
  }
}



